userDefinedLinkHintCss:
#vimiumHintMarkerContainer div.internalVimiumHintMarker,
#vimiumHintMarkerContainer div.vimiumHintMarker {
  padding: 3px 4px;
  background: #212121;
  border: none;
  box-shadow: 0 1px 3px rgba(0, 0, 0, .12), 0 1px 2px rgba(0, 0, 0, .24)
}

#vimiumHintMarkerContainer div span {
  color: #fff;
  text-shadow: none
}

#vimiumHintMarkerContainer div>.matchingCharacter {
  opacity: .4
}

#vimiumHintMarkerContainer div>.matchingCharacter~span {
  color: #43b1e9
}

#vomnibar {
  background: #212121;
  border: 0;
  box-shadow: 0 1px 3px rgba(0, 0, 0, .12), 0 1px 2px rgba(0, 0, 0, .24);
  top: 18px
}

@media screen and (min-width: 640px) {
  #vomnibar {
    left: 50%;
    margin-left: -320px;
    width: 640px
  }
}

#vomnibar .vomnibarSearchArea,
#vomnibar input {
  color: #eee;
  background: transparent;
}

#vomnibar input {
  border: 0;
  box-shadow: none;
  font-size: 20px;
  height: initial;
  padding: 16px 24px
}

#vomnibarInput::selection {
  background-color: #5A7EA4
}

#vomnibar .vomnibarSearchArea {
  border: 0;
  padding: 0
}

#vomnibar ul {
  background: #212121;
  padding: 0;
  margin: 0;
  overflow: hidden;
  border-top: 1px solid #333
}

#vomnibar li {
  background: var(--bg);
  border-bottom: 1px solid #333;
  display: flex;
  line-height: 1;
  padding: 12px 16px 12px 24px
}

#vomnibar li .vomnibarTopHalf,
#vomnibar li .vomnibarBottomHalf {
  margin: 0;
  padding: 0
}

#vomnibar li .vomnibarTopHalf {
  display: flex;
  flex: 0 0 67%;
  margin-right: 16px
}

#vomnibar li .vomnibarBottomHalf {
  display: flex;
  align-items: center;
  flex: 0 0 33%
}

#vomnibar li .vomnibarSource.vomnibarNoInsertText,
#vomnibar li .vomnibarSource.vomnibarInsertText {
  display: none;
}

#vomnibar li .vomnibarSource {
  color: #ffffff65;
  display: block;
  flex-shrink: 0;
  font-size: 14px;
  font-weight: bold;
  line-height: 2;
  margin-right: 12px;
  text-transform: capitalize
}

#vomnibar li .vomnibarTitle,
#vomnibar li .vomnibarIcon,
#vomnibar li .vomnibarUrl {
  line-height: 1.6;
}

#vomnibar li em,
#vomnibar li .vomnibarTitle {
  color: #eee
}

#vomnibar li .vomnibarTitle {
  display: block;
  margin: 0;
  overflow: hidden;  
  text-overflow: ellipsis;
  white-space: nowrap
}

#vomnibar li .vomnibarIcon {
  display: block;
  flex-shrink: 0;
  height: 16px;
  width: 16px;
  margin: 0;
  padding: 0 6px 0 0
}

#vomnibar li .vomnibarUrl {
  color: #777;
  padding-right: 6px;
  display: block;
  margin: 0;
  overflow: hidden;
  text-overflow: ellipsis;
  white-space: nowrap
}

#vomnibar li .vomnibarMatch {
  color: #43b1e9;
  font-weight: normal
}

#vomnibar li .vomnibarTitle .vomnibarMatch {
  color: #43b1e9
}

#vomnibar li.vomnibarSelected {
  background-color: #313131
}

div.vimiumHUD {
  background: #212121;
  border: none;
  box-shadow: 0 1px 3px rgba(0, 0, 0, .12), 0 1px 2px rgba(0, 0, 0, .24)
}

div.vimiumHUD span#hud-find-input,
div.vimiumHUD .vimiumHUDSearchAreaInner {
  color: #fff
}

div.vimiumHUD .hud-find {
  background-color: transparent;
  border: none
}

div.vimiumHUD .vimiumHUDSearchArea {
  background-color: transparent
}
